from autof2.readf2 import parse
from autof2.navigation import navigation
from autof2.interface.send_data import SendData
import time
import make_json

def get_data(lotts):
    data = []
    send = SendData()
    navigation.to_input_purchase('31/12/17')
    send.send('{F12}')
    time.sleep(.1)
    send.send('+{F11}')
    time.sleep(.2)
    for lott in lotts:
        send.send('{F7}')
        send.send(lott)
        send.send('{ENTER}')
        send.send('{F11}')
        time.sleep(.3)
        data.append(parse.get_input_purchase_info())
        send.send('{F12 2}')
        time.sleep(.3)
    return data


